//if , else if ,else ,&&
package coreJava;

public class Ex10_ElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10;		
		int num2=20;
		int num3=30;
		
	if (num1>=num2 && num1>=num3)
	System.out.println("number is smaller" +num1);
	
	else if (num2 >= num1 && num2 >= num3)
	System.out.println("number is larger" +num2);
	
	else
	System.out.println("number is larger" +num3);
		
	}
	}

