//Loops - Execution till condition is met
package coreJava;

public class Ex12_Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 5;
		for(int i=0; i<=5; i++)
		{
			System.out.println(+i);
		}
		
		for(int i=5; i>=0; i--)
		{
			System.out.println(+i);
		}
	}

}
