//to print even number from 1 to 10
package coreJava;

public class Ex12a_Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<=10; i++){
			if (i%2==0){
			System.out.println("even number: " +i);
		}
	}
		for(int i=0; i<=10; i++){
			if (i%2!=0){
			System.out.println("odd number:" +i);
		}
	}
	}
}


